String.prototype.hexEncode = function () {
	var hex, i;
	var result = "";
	for (i = 0; i < this.length; i++) {
		hex = this.charCodeAt(i).toString(16);
		result += ("000" + hex).slice(-4);
	}
	return result;
};

String.prototype.hexDecode = function () {
	var j;
	var hexes = this.match(/.{1,4}/g) || [];
	var back = "";
	for (j = 0; j < hexes.length; j++) {
		back += String.fromCharCode(parseInt(hexes[j], 16));
	}
	return back;
};

String.prototype.is_number = function () {
	return !isNaN(parseFloat(this)) && !isNaN(this - 0);
};

if (!String.prototype.startsWith) {
	String.prototype.startsWith = function (searchString, position) {
		position = position || 0;
		return this.substr(position, searchString.length) === searchString;
	};
}

String.prototype.pad_with_spaces = function (spaces) {
	if (this.length < spaces) {
		return this + ' '.repeat(spaces - this.length);
	}
	return this;
}

String.prototype.stuffus = function (value) {
	var index = this.indexOf("_");
	if (index >= 0) {
		return this.substr(0, index) + value + this.substr(index + 1, this.length);
	}
	return this;
}

function only_or_first(str) {
	if (str != undefined) {
		if (Array.isArray(str)) {
			if (str.length > 0) {
				return str[0];
			} else {
				return {};
			}
		} else {
			return str;
		}
	} else {
		return "";
	}
}

Array.prototype.chunk = function (n) {
	if (!this.length) {
		return [];
	}
	return [this.slice(0, n)].concat(this.slice(n).chunk(n));
};

Array.prototype.last = function () {
	return this[this.length - 1];
}

Array.prototype.remove_duplicates = function () {
	var temp = {};
	for (var i = 0; i < this.length; i++)
		temp[this[i]] = true;
	return Object.keys(temp);
};

if (!Array.prototype.includes) {
	Object.defineProperty(Array.prototype, "includes", {
		enumerable: false,
		value: function (obj) {
			var newArr = this.filter(function (el) {
				return el == obj;
			});
			return newArr.length > 0;
		}
	});
}

Number.prototype.to_percent = function (min, max) {
	return 100.0 * (this - min) / (max - min);
};

// Change a volume scale from dB to per cent
Number.prototype.dB_to_percent = function () {
	var base_pc = 30;
	var base_db = -25.0;
	var increment = 0.5;
	if (this <= -74) {
		base_db = -90.0;
		increment = 4.0;
		base_pc = 0;
	} else if (this <= -53) {
		base_db = -74.0;
		increment = 3.0;
		base_pc = 4;
	} else if (this <= -35) {
		base_db = -53.0;
		increment = 2.0;
		base_pc = 11;
	} else if (this <= -25) {
		base_db = -35.0;
		increment = 1.0
		base_pc = 20;
	}
	return (this - base_db) / increment + base_pc;
}

// Change a volume scale from per cent to dB
Number.prototype.percent_to_dB = function () {
	var base_pc = 30;
	var base_db = -25.0;
	var increment = 0.5;
	if (this <= 4) {
		base_pc = 0;
		increment = 4;
		base_db = -90.0;
	} else if (this <= 11) {
		base_pc = 4;
		increment = 3;
		base_db = -74.0;
	} else if (this <= 20) {
		base_pc = 11;
		increment = 2;
		base_db = -53.0;
	} else if (this <= 30) {
		base_pc = 20;
		increment = 1;
		base_db = -35.0;
	}
	return (this - base_pc) * increment + base_db;
}

// From seconds
Number.prototype.tohmMSS = function () {
	var seconds = Math.floor(this), hours = Math.floor(seconds / 3600);
	seconds -= hours * 3600;
	var minutes = Math.floor(seconds / 60);
	seconds -= minutes * 60;
	if (hours > 0) {
		if (minutes < 10) { minutes = "0" + minutes; }
		if (seconds < 10) { seconds = "0" + seconds; }
		return hours + ":" + minutes + ":" + seconds;
	} else {
		if (seconds < 10) { seconds = "0" + seconds; }
		return minutes + ":" + seconds;
	}
}

